catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    // Define the environment abbreviations
    var envAbbr = {
        'Disaster Recovery': 'DR',
        'Pre-Production': 'PPE',
        'Development': 'DEV',
        'Production': 'PROD'
    };

    // Get the display value of the service_name reference variable
    g_form.getReference('service_name', function(service) {
        // Replace spaces with underscores in the service_name display value
        var serviceName = service.name.replace(/\s+/g, '_'); 

		// Limit the service name to a maximum of 10 characters
        serviceName = serviceName.substring(0, 15);

		// Get the value of the env variable
        var env = g_form.getValue('env');

        // Get the abbreviation for the selected environment
        // If env is a key in the envAbbr object, envShort will be assigned the corresponding value (like 'DR' for 'Disaster Recovery').
        // If env is not a key in the envAbbr object, envShort will be assigned the value of env itself.
        var envShort = envAbbr[env] || env;

        // Construct the service account name using the specified format and convert to lowercase
        var svcAccName = ('serv_' + serviceName + '_build_' + envShort).toLowerCase();

        // Set the value of the svc_acc_name variable
        g_form.setValue('svc_acc_name', svcAccName);
    });

}
