catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    //if (isLoading || newValue == '') {
    if (isLoading) {
        return;
    }
    var currentAction = g_form.getValue('action');
    if (currentAction == 'create_perm') {
        if (newValue == '') {
            // Set the field to be hidden
            g_form.setMandatory('server', false);
            g_form.setDisplay('server', false);

        }
        var sysID_ServiceAccount = g_form.getValue('svc_account');
        //alert('sysID_ServiceAccount: ' + sysID_ServiceAccount);
        var ga = new GlideAjax('LLoyServiceAccountUtils');
        ga.addParam('sysparm_name', 'checkAccountType');
        ga.addParam('sysparm_sys_id', sysID_ServiceAccount);
        ga.getXMLAnswer(function(response) {
            var answer = response;
            //alert('Account Type: ' + answer);
            if (answer == 'Windows') {
                g_form.setDisplay('server', true);
                g_form.setMandatory('server', true);
            } else {
                // Set the field to be hidden
                g_form.setMandatory('server', false);
                g_form.setDisplay('server', false);
            }
        });
    }

}
