sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

      // Create a new GlideAjax object to call a server-side script include
    var ga = new GlideAjax('LLoyServiceAccountUtils');
    // Add a parameter to specify the function to call in the script include
    ga.addParam('sysparm_name', 'checkServiceAccountName');
    // Add a parameter to pass the new value of the field to the script include
    ga.addParam('sysparm_account_name', newValue);
    ga.getXMLAnswer(function(response) {
        // Make the asynchronous call to the server and handle the response
        var result = JSON.parse(response);
        // Parse the JSON response from the server
        if (result.exists) {
            // If the service account name already exists
            // Display an error message at the top of the form
            //g_form.addErrorMessage(newValue + ' already exists in the Service Accounts table. Please choose a different name.');
            g_form.setValue('account_name', ''); // Clear the name field after showing the message
            // Show an error message next to the 'slt_name' field
            g_form.showFieldMsg('account_name', newValue + ' already exists in the Service Accounts table. Please choose a different name.', 'error');
            setTimeout(function() {
                var nameVar = g_form.getControl('account_name');
                // Get the control (HTML element) for the 'svc_acc_name' field
                if (nameVar) {
                    $(nameVar).focus(); // Use jQuery to set focus
                    // Set focus to the 'slt_name' field using jQuery
                }
            }, 100); // Additional delay to ensure the field is cleared before focusing
            // Delay the focus action to ensure the field is cleared first
        } else {
            g_form.hideFieldMsg('account_name', true);
            // Hide any field messages for the 'slt_name' field
            g_form.clearMessages(); // Clear all form messages
            // Clear all form messages (error, info, warning)

        }
    });

}
