sys_script_include.script:
var LLoyServiceAccountUtils = Class.create();
LLoyServiceAccountUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    checkAccountType: function() {
        var referenceId = this.getParameter('sysparm_sys_id');
        var accType = '';
        var svcAccRecord = new GlideRecord('x_llol_service_acc_records');
        if (svcAccRecord.get(referenceId)) {
            accType = svcAccRecord.account_type;
        }
        return accType;
    },
    checkServiceAccountName: function() {
        var serviceAccName = this.getParameter('sysparm_account_name');
        var result = {
            exists: false,
        };

        var serviceAccGR = new GlideRecord('x_llol_service_acc_records');
        serviceAccGR.addQuery('account_name', serviceAccName);
        serviceAccGR.query();
        if (serviceAccGR.next()) {
            result.exists = true;
        }

        return JSON.stringify(result);
    },
    type: 'LLoyServiceAccountUtils'
});
